import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { toast } from "react-toastify";
import { reactLocalStorage } from "reactjs-localstorage";

let BearerToken = reactLocalStorage.get("Token", false)
    
// SignIn

export const SignIn = createAsyncThunk("SignIn",async (body, { rejectWithValue }) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_BASE_URL}/api/auth/login`,body);
      toast.success("Successful");
      reactLocalStorage.set("Token",response?.data?.token?.original?.access_token);
      // reactLocalStorage.set("id",response?.data?.currentUser?.user_id);
      // reactLocalStorage.set("name",response?.data?.currentUser?.user_name);
      // reactLocalStorage.set("email",response?.data?.currentUser?.user_email);
      // reactLocalStorage.set("employee_id",response?.data?.currentUser?.employee_id);
      // reactLocalStorage.set("national_id",response?.data?.currentUser?.national_id);
      // reactLocalStorage.set("role_id",response?.data?.currentUser?.role_id);
      // reactLocalStorage.set("role_name",response?.data?.currentUser?.role_name);

      let Permission =  JSON.stringify(response?.data?.currentUser?.permissions);
      reactLocalStorage.set("Permissions",Permission);
      console.log("Permissions",Permission)

      return response;

    } catch (err) {
      toast.error(err?.response?.data?.message);
      return rejectWithValue(err);
    }
  }
);


// SignOut 

export const SignOut = createAsyncThunk("SignOut",async (body, { rejectWithValue }) => {
  try {
    const response = await axios.post(`${process.env.REACT_APP_BASE_URL}/api/auth/logout`,body,{
      headers: {
        Authorization: `Bearer ${BearerToken}`,
      },
    });
    toast.success(response?.data?.message);
    reactLocalStorage.clear();
    return response;

  } catch (err) {
    toast.error(err?.response?.data?.message);
    return rejectWithValue(err);
  }
}
);

export const ToggleBar = createAsyncThunk("ToggleBar",async(body) => ({
  data: body,
}))


// Reducer

export const signIn = createSlice({
  name: "signIn",
  initialState: {
    data: [],
    Logout:[],
    toggle:[],
    loading: false,
    error: null,
  },
  reducers: {},

  extraReducers: (builder) => {
    builder
      .addCase(SignIn.pending, (state) => {
        state.loading = true;
      })

      .addCase(SignIn.fulfilled, (state, action) => {
        state.loading = false;
        state.data.push(action.payload);
      })

      .addCase(SignIn.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })

      .addCase(SignOut.pending, (state) => {
        state.loading = true;
      })

      .addCase(SignOut.fulfilled, (state, action) => {
        state.loading = false;
        state.Logout.push(action.payload);
      })

      .addCase(SignOut.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })

      .addCase(ToggleBar.pending, (state) => {
        state.loading = true;
      })

      .addCase(ToggleBar.fulfilled, (state, { payload }) => {
        state.loading = false;
        console.log("ToggleBar ", payload?.data)
        state.toggle = payload?.data;
      })


      .addCase(ToggleBar.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  },

});

export default signIn.reducer;
